mapper.update:
UPDATE
	u_user
SET
	email = #{email}
WHERE
	nickname = #{nickname}

UPDATE
	u_user
SET
	ecode = #{ecode}
WHERE
	nickname = #{nickname}

mapper.select:
SELECT
	COUNT(0)
FROM
	u_user
WHERE
	nickname = #{nickname}
	AND email = #{email}
	AND ecode = #{ecode}

mapper.update:
UPDATE
	u_user
SET
	pswd = #{pswd}
WHERE
	nickname = #{nickname}
